import UIKit

enum Drawing {
    static let squareSize: CGSize = .init(width: 100, height: 100)
    static let allSystemColor: [UIColor] = [.systemBlue, .systemCyan, .systemFill,
                                            .systemGray, .systemMint, .systemPink,
                                            .systemTeal, .systemBrown, .systemOrange]
    
    enum AddSquareEvent {
        struct Request {
            let bounds: CGSize
        }

        struct Response {
            let rect: CGRect
            let color: UIColor
        }
        
        typealias ViewModel = Response
    }
    
    enum DrawEvent {
        struct Request {
            let bounds: CGRect
        }
        
        typealias Response = AddSquareEvent.Response
        typealias ViewModel = AddSquareEvent.ViewModel
    }
}
